mapper.select:
select

from activity a
where a.id = #{id,jdbcType=INTEGER}

select a.id, a.title, a.address, a.begin_time beginTime, a.end_time endTime, f.file_path coverPath from activity a left join upload_file f on f.id = a.upload_file_id where 1 = 1 and a.end_time > now()
and a.district_id = #{districtId,jdbcType=INTEGER}
and a.active = #{active,jdbcType=BIT}
order by a.id desc

select a.id, a.title, a.address, a.begin_time beginTime, a.end_time endTime, f.file_path coverPath from activity a inner join upload_file f on f.id = a.upload_file_id where 1 = 1 and a.end_time < now()
and a.district_id = #{districtId,jdbcType=INTEGER}
and a.active = #{active,jdbcType=BIT}
order by a.id desc

select a.id, a.title, a.address, a.begin_time beginTime, a.end_time endTime, f.file_path coverPath
from activity a
inner join activity_enroll e on e.activity_id = a.id
inner join upload_file f on f.id = a.upload_file_id
where 1 = 1

    and e.user_id = #{id,jdbcType=INTEGER}


    and a.district_id = #{districtId,jdbcType=INTEGER}


    and e.active = #{active,jdbcType=BIT}

order by a.id desc
